mapper.select:
select * from ( select
, m_sales_volume*100/(select sum(m_sales_volume) from tb_sta_cat t1 where t1.YEAR = t.year and t1.MONTH = t.MONTH and t1.PROVINCE = t.PROVINCE and t1.PLATFORM = t.PLATFORM and t1.S_CAT1_NAME = t.S_CAT1_NAME) salesPercent, M_SLAES_COUNT*100/(select sum(M_SLAES_COUNT) from tb_sta_cat t1 where t1.YEAR = t.year and t1.MONTH = t.MONTH and t1.PROVINCE = t.PROVINCE and t1.PLATFORM = t.PLATFORM and t1.S_CAT1_NAME = t.S_CAT1_NAME) countPercent from tb_sta_cat t
ORDER BY ${orderBy} desc
) t2 where ID > (#{pageNo}-1)*#{pageSize} limit #{pageSize}

select * from (
SELECT CAT2_NAME cat2Name,sum(M_SLAES_COUNT) totalCount,sum(M_SALES_VOLUME), (@i:=@i+1) as row totalVolume from tb_sta_cat,(select @i:=0) as it where CAT2_NAME is not null GROUP BY CAT2_NAME ) t
ORDER BY totalCount desc,totalVolume DESC limit (#{pageNo}-1)*#{pageSize},{pageSize}

