        AND CITY = #{city,jdbcType=VARCHAR}


and INDEX_TYPE ='电子商务店铺数'
ORDER BY date desc limit 1) b
UNION
select * from (
select sum(index_value) total from tb_sta_total


        AND date between #{dateBegin,jdbcType=VARCHAR} and #{dateEnd,jdbcType=VARCHAR}


        AND PROVINCE = #{province,jdbcType=VARCHAR}


        AND CITY = #{city,jdbcType=VARCHAR}


and INDEX_TYPE not in('电子商务从业人数','电子商务店铺数')
GROUP BY PROVINCE,CITY,INDEX_TYPE
ORDER BY CITY,INDEX_TYPE) c
) t

SELECT * from ( select t1.dateStr,t1.PROVINCE,t1.city,t1.qys,t2.cyrs,t1.qysmom,t2.cyrsmom from (SELECT date dateStr,PROVINCE,city,sum(index_value) qys,sum(mom) qysmom from tb_sta_total where index_type = '电子商务企业数'
GROUP BY date,PROVINCE,city) t1 left JOIN ( SELECT date dateStr,PROVINCE,city,sum(index_value) cyrs,sum(mom) cyrsmom from tb_sta_total where index_type = '电子商务从业人数'
GROUP BY date,PROVINCE,city) t2 on t1.city = t2.city and t1.dateStr=t2.dateStr and t1.PROVINCE=t2.PROVINCE )c limit #{pageRow},#{pageSize}

